import axios from"axios";export default class GoogleTranslate{static async translate(t,e,r){const a=await GoogleTranslate.requestTranslation(t,e,r);return GoogleTranslate.getSentencesFromJSON(a)}static async requestTranslation(t,e,r){if(r.length>=5e3)throw new Error("Maximum number of characters exceeded: 5000");const a=new URLSearchParams({client:"gtx",dt:"t",sl:t,tl:e,q:r}).toString();try{return(await axios.get("https://translate.googleapis.com/translate_a/single?"+a,{responseType:"arraybuffer"})).data}catch(t){throw t}}static getSentencesFromJSON(t){const e=JSON.parse(t);let r="";if(!e||!e[0])throw new Error("Google detected unusual traffic from your computer network, try again later (2 - 48 hours)");return e[0].forEach((t=>{r+=t[0]||""})),r}}